--


--
-- Disparadores `energia_entregada`
--
DELIMITER $$
CREATE TRIGGER `calcular_balance_entregada` AFTER INSERT ON `energia_entregada` FOR EACH ROW BEGIN
    UPDATE balance_mensual
    SET energia_mensual_entregada = (
        SELECT COALESCE(SUM(energia), 0) + COALESCE((SELECT total_ventas FROM ventas_totales WHERE id_fecha = NEW.id_fecha), 0)
        FROM energia_entregada
        WHERE id_fecha = NEW.id_fecha
    )
    WHERE id_fecha = NEW.id_fecha;
END
$$
