DELIMITER $$
CREATE TRIGGER `calcular_balance_entregada_actualizacion` AFTER UPDATE ON `energia_entregada` FOR EACH ROW BEGIN
    UPDATE balance_mensual
    SET energia_mensual_entregada = 
        (
            (SELECT COALESCE(total_ventas, 0) FROM ventas_totales WHERE id_fecha = NEW.id_fecha) +
            (SELECT COALESCE(SUM(energia), 0) FROM energia_entregada WHERE id_fecha = NEW.id_fecha)
        )
    WHERE id_fecha = NEW.id_fecha;
END
$$
