    SET energia_eliminar = OLD.energia;

    UPDATE balance_mensual
    SET energia_mensual_entregada = (
        SELECT COALESCE(energia_mensual_entregada, 0) - energia_eliminar
        FROM balance_mensual
        WHERE id_fecha = OLD.id_fecha
    )
    WHERE id_fecha = OLD.id_fecha;
END
